import './App.css';
import ChatView from './Components/ChatScreen/ChatView';
import SideBar from './Components/SideBar/SideBar';
import {BrowserRouter ,Route, Redirect, Routes, useLocation, useNavigate} from "react-router-dom";
import View from './View/View';

import Auth from './Components/Auth';
import { useEffect, useState } from 'react';
import ChatProvider from './ChatProvideContext';
import Chat from './Components/ChatScreen/Chat';
import StarterPage from './Components/StarterPage';

function App() {

  const [state, setState] = useState(0)
  const user = JSON.parse(localStorage.getItem("userInfo"));
  const navigate=useNavigate();


  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("userInfo"));

    if (user){
      setState(1)
      // navigate('/')
    } 
    else{
      setState(0)
      // navigate("/chats");
    }
  }, [setState,state,user]);
  // console.log(state);
  return (
    <div className="App h-screen no-scrollbar">
      <BrowserRouter>
      <ChatProvider>
      {state ? <View/> : <Auth/>}
      {/* <Routes>
      <Route exact path="/" 
      element={<Auth/>}/>
      <Route exact path="/chats" 
      element={<View/>}/>
      </Routes> */}

     {/* {state ? 
      <Routes>
      <Route exact path="/" 
      element={
      <div className="flex flex-row">
          <SideBar/>
          <StarterPage/>
      </div>
      }/>

      <Route exact path="/:chatId" element={
        <div className="flex flex-row">

        <SideBar/>
        <Chat/>
        </div>
      } />
      </Routes>
      :
      <Auth/>

      } */}

      </ChatProvider>
    </BrowserRouter>
    </div>
  );
}

export default App;
