import ChatView from './Components/ChatScreen/ChatView';
import SideBar from './Components/SideBar/SideBar';
import {BrowserRouter ,Route, Redirect, Routes, useLocation, useNavigate} from "react-router-dom";
import View from './View/View';

import Auth from './Components/Auth';
import { useEffect, useState } from 'react';
import ChatProvider from './ChatProvideContext';

function App() {

  const [state, setState] = useState(false)
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("userInfo"));

    if (user) 
      setState(true)
    // Navigate("/chats");
  }, [state]);
  console.log(state);
  return (
    <div className="App max-h-screen no-scrollbar">
      <BrowserRouter>
      <ChatProvider>
      <Routes>


     <Route exact path="/id" element={
     <div className="flex flex-row  h-5/6">
     <SideBar className='mob hidden sm:flex' />
     <ChatView className=''/>
     </div>
     } />
     {/* <Route exact path="/id">
       <SideBar/>
       <ChatView/>
     </Route> */}
      {/* <Route exact path="/" 
      element={<Auth/>}>

      </Route> */}
      <Route exact path="/" 
      element={state?<SideBar/>:<Auth/>}>

      </Route>
      </Routes>

      </ChatProvider>
    </BrowserRouter>
    </div>
  );
}

export default App;
