            <div className="px-10 w-11/12   absolute bottom-10 mt-auto  text-right bg-slate-400">
                <form className="flex h-full">
                    <button>image</button>
                    <input type='text' className='w-full  fex flex-grow mx-3  rounded-2xl ' />
