import axios from 'axios';
import React, { useState } from 'react'
import { ChatState } from '../../ChatProvideContext';

function UpdateGroup() {
    const [groupChatName, setGroupChatName] = useState();
    const [renameloading, setRenameLoading] = useState(false);
    const { selectedChat, setSelectedChat, user,fetching,setFetching } = ChatState();
    const handleRename = async () => {
        if (!groupChatName) return;

        try {
          setRenameLoading(true);
          const config = {
            headers: {
              Authorization: `Bearer ${user.token}`,
            },
          };
          const { data } = await axios.put(
            `/chat/rename`,
            {
              chatId: selectedChat._id,
              chatName: groupChatName,
            },
            config
          );

          console.log(data._id);
          // setSelectedChat("");
          setSelectedChat(data);
          setFetching(!fetching);
          setRenameLoading(false);
        } catch (error) {
          console.log('error occured');
          setRenameLoading(false);
        }
        setGroupChatName("");
      };
    return (
        <div className=" absolute z-50 w-full max-w-screen-md h-full  border-l-orange-400">
            <div className="grid gap-2 w-full">
                <div className="w-full">

                    <div className="flex flex-row">
                    <input type="text" placeholder="Gp Name"value={groupChatName}
                    onChange={(e) => setGroupChatName(e.target.value)} />
                    <button onClick={handleRename}>change</button>
                    </div>
                </div>
                <div>
                    <input type="text" placeholder="add users"/>                    
                </div> 

                <div className="m-1 flex bg-slate-100 ">
                    {selectedChat.users.map(u=>(
                        <p className="mx-1">{u.email}</p>
                    ))}

                </div>
                <div>
                    <button>remove</button>
                </div>
            </div>
        </div>
    )
}

export default UpdateGroup
