            </div>

            <div className='grid'>
            <label>Email</label>
            <input type="text" value={password} onChange={(e)=>setPassword(e.target.value)}/>
            </div>



            <button className='border-l-pink-300 p-5 '
            onClick={handleSubmit}>sigggggn</button>

        </div>
    )
}

export default SignIn
